function llamar1() {
    /*SliderAutomatico */

    
    let images = ['../img/thumbnail1.jpg', '../img/thumbnail2.jpg', '../img/thumbnail3.jpg',
        '../img/thumbnail4.jpg'];

    let ind = 0;
    let tiempo = 9995;
    let frontImage = document.getElementById("front");
    let backImage = document.getElementById("back");

    function slideImages() {
        console.log("Entro a slider");
        frontImage.src = images[ind];
        frontImage.style.animation = "desvanecer 10s infinite ease-in-out"
        if (ind < 3) {
            ind++;
            setearBack(ind + 1);

        } else {
            ind = 0;
            setearBack(ind);
        }
        setearBack(ind);
    }

    /*const interval = () => {
        setInterval(slideImages, tiempo);
    }*/

    const setearBack = function (i) {
        console.log("entro a setear back");
        console.log("indice:" + i);
        setTimeout(() => {
            backImage.src = images[i];
        }, 3000);
    };


    slideImages();

    /*SliderAutomatico */

}

const runScript1 = () => {
    llamar1();
}

