function llamar2() {
    /*RESIZE*/
    function resizE() {

        const textarea = document.getElementById("textareaP");
        console.log("texteare: " + textarea);

        textarea.addEventListener("keyup", e => {
            console.log("Entro a text");
            textarea.style.height = "46px";
            let scHeight = e.target.scrollHeight;
            textarea.style.height = `${scHeight}px`;

        });
    }
    resizE();
    /*RESIZE*/

    /*ALBUManimacion */
    function albumImages() {
        let canvas5 = document.getElementById("small-5");
        let first = Math.floor(Math.random() * (4 - 1) + 1);
        var contador = 0;
        while (contador < 2) {

            if (contador == 0) {

                random(first);

                if (first == 4) {
                    first = first - 1;
                } else {
                    first++;
                }

            } else {
                setTimeout(() => {
                    random(first);
                }, 10000);
                setTimeout(() => {
                    canvas5.style.animation = "album 30s infinite ease-in-out";
                }, 12000);
            };
            contador++;
        }
    }



    const random = function (opcion) {

        let canvas1 = document.getElementById("small-1");
        let canvas2 = document.getElementById("small-2");
        let canvas3 = document.getElementById("small-3");
        let canvas4 = document.getElementById("small-4");

        console.log("Entro a random");
        switch (opcion) {

            case 1:
                fadeOff(canvas1);
                setTimeout(() => {
                    fadeOff(canvas4);
                }, 3000);
                break;
            case 2:
                fadeOff(canvas2);
                setTimeout(() => {
                    fadeOff(canvas3);
                }, 3000);
                break;
            case 3:
                fadeOff(canvas3);
                setTimeout(() => {
                    fadeOff(canvas2);
                }, 3000);
                break;
            default:
                fadeOff(canvas4);
                setTimeout(() => {
                    fadeOff(canvas1);
                }, 3000);
                break;

        }
    }
    const fadeOff = function (canvas) {
        canvas.style.animation = "album 30s infinite ease-in-out";
    };

    albumImages();
    /*ALBUManimacion */
}

const runScript2= ()=>{
    llamar2();
}